	private static Pattern pattern = Pattern.compile(EMAIL_PATTERN);
	
	/**
	 * @author JO031U
	 * @param email
	 * @return
	 */
    static String isValid(String email) {
        //Empty emails
        if(email == null || email.isEmpty()){
            return NO;
        }
        //The lenght must be 18 no more no less --> abcde@hogwarts.com
        if(email.length() != 18){
            return NO;
        }
        
        //Search for @hogwarts.com
        if(!email.substring(5).equals(HOWARDS)) {
        	return NO;
        }
        //REGEX NAME
        for(char ch : email.substring(0, 5).toCharArray()) {
        	if(ch >= 65 && ch <= 90) {
        		return NO;
        	}
        }
        Matcher matcher = pattern.matcher(email);
		if(matcher.matches())
			return YES;
		else
			return NO;
    }
    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        String s = in.next();
        String result = isValid(s);
        System.out.println(result);
        in.close();
    }
